package hexlet.code;import picocli.CommandLine;import picocli.CommandLine.Command;import picocli.CommandLine.Parameters;import picocli.CommandLine.Option;import java.util.concurrent.Callable;@Command(        name = "gendiff",        description = "Compares two configuration files and shows a difference.",        mixinStandardHelpOptions = true,        version = "gendiff 1.0",        synopsisHeading = "%nUsage:")public final class App implements Callable<Void> {    @Parameters(index = "0", description = "path to first file", arity = "0..1", hideParamSyntax = true)    private String filepath1;    @Parameters(index = "1", description = "path to second file", arity = "0..1", hideParamSyntax = true)    private String filepath2;    @Option(names = {"-f", "--format"},            description = "output format [default: ${DEFAULT-VALUE}]",            defaultValue = "stylish",            hideParamSyntax = true)    private String format;    @Option(names = {"-h", "--help"}, description = "Show this help message and exit.")    private boolean helpFlag;    @Option(names = {"-V", "--version"}, description = "Print version information and exit.")    private boolean versionFlag;    public static void main(String[] args) {        int exitCode = new CommandLine(new App()).execute(args);        System.exit(exitCode);    }    @Override    public Void call() throws Exception {        if (helpFlag) {            CommandLine.usage(this, System.out);        }        System.out.println(Differ.generate(filepath1, filepath2, format));        return null;    }}